import styled from 'styled-components';

export const TimerButton = styled.a`
  border-radius: 26.5px;
  font-family: 'Poppins', sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: 13px;
  line-height: 19px;
  padding: 15px 19px;
  color: #D7E0FF;
  &:active {
    background: #EFF1FA;
    color: #1E213F;
    border-radius: 26.5px;
  }
  &:hover{
    cursor: pointer;
  }
`


