import React from 'react';
import styled from 'styled-components';

export const TimerSettingButton = styled.div`
  font-family: 'Poppins', sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: 13px;
  line-height: 19px;
  padding: 15px 19px;
  color: #1E213F;
  background: #F87070;
  border-radius: 26.5px;
  text-align: center;
  cursor:pointer;
  margin-left: 20px;
  &:hover{
    opacity: 30%;
    color: #fff;
  }
`

